import React, { useState } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
  Modal,
  Alert,
} from "react-native";
import EventCalendar from "react-native-events-calendar";
//get the size of device
let { width } = Dimensions.get("window");

const Event = () => {
  let events = [];
  const [modalVisible, setModalVisible] = useState(false);

  const createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  const eventClicked = (event) => {
    setModalVisible(!modalVisible);
  };

  createEvent(
    "2021-03-14 23:00:00",
    "2021-03-14 23:30:00",
    "Test",
    "This is test"
  );

  return (
    <SafeAreaView>
      <View style={styles.container}>
        <EventCalendar
          eventTapped={eventClicked}
          events={events}
          width={width - width * 0.1}
          size={90}
          scrollToFirst
        />
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        backgroundColor="red"
      >
        <View style={{ backgroundColor: "red" }}></View>
      </Modal>
    </SafeAreaView>
  );
};

export default Event;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
