import React from "react";
import { Font } from "expo";
import {
  StyleSheet,
  View,
  Text,
  ImageBackground,
  Image,
  Linking,
  ScrollView,
  TouchableOpacity,
} from "react-native";

const Contact = () => {
  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <ScrollView style={styles.container}>
        <View style={styles.title}>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            Contact Us
          </Text>
          <Text style={styles.text}>
            University of Florida College of Design, Construction and Planning
            {"\n"}
            {"\n"}
            Address: Architecture Building, 1480 Inner Road, Gainesville, FL
            32611{"\n"}
            Phone Number: (352) 392-4836{"\n"}
            Fax: (352) 392-7266{"\n"}
            =========== ADD MORE INFO=========
          </Text>
          <Text style={styles.text}>Connect:</Text>
          <TouchableOpacity
            onPress={() => {
              Linking.openURL("https://instagram.com/ufdcp/");
            }}
            style={styles.link}
          >
            <Image
              style={{ width: 30, height: 30 }}
              resizeMode="contain"
              source={require("../assets/ig.png")}
            />
            <Text>Instagram</Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => {
              Linking.openURL("https://instagram.com/ufdcp/");
            }}
            style={styles.link}
          >
            <Image
              style={{ width: 30, height: 30 }}
              resizeMode="contain"
              source={require("../assets/twitter.png")}
            />
            <Text>Twitter</Text>
          </TouchableOpacity>
          <Text
            onPress={() => {
              Linking.openURL("https://www.facebook.com/UF.dcp");
            }}
          >
            Facebook
          </Text>
          <Text
            onPress={() => {
              Linking.openURL("https://www.linkedin.com/company/ufdcp/");
            }}
          >
            Linkedin
          </Text>
        </View>
      </ScrollView>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
    marginTop: "10%",
  },
  link: {
    flex: 1,
    flexDirection: "row",
    paddingBottom: "5%",
    backgroundColor: "red",
    marginRight: "73%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
  },
  text: {
    color: "white",
    fontWeight: "bold",
    paddingBottom: "2%",
  },
});

export default Contact;
