import React, { useState } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
} from "react-native";
import EventCalendar from "react-native-events-calendar";
import * as Calendar from "expo-calendar";
import * as Permissions from "expo-permissions";
import { render } from "react-dom";

//get the size of device
let { width } = Dimensions.get("window");

export default class Event extends React.Component {
  constructor() {
    super();
    var events = [];
  }

  static navigationOptions = ({ navigation }) => {
    return {
      headerTitle: "Password",
      headerTintColor: "#000",
      headerRight: (
        <View style={styles.headerRight}>
          <Button
            style={styles.buttonHeader}
            color="#000"
            title="Save"
            onPress={navigation.getParam("updatePassword")}
          />
        </View>
      ),
    };
  };

  createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  eventClicked = (event) => {
    alert(
      `${event.title} \nEvent Start: ${event.start}\nEvent End: ${event.end}\nDetails: ${event.summary}
      `
    );
  };

  render() {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.container}>
          <EventCalendar
            eventTapped={this.eventClicked}
            events={this.events}
            width={width}
            size={90}
            scrollToFirst
          />
        </View>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
