import React from "react";
import { Font } from "expo";
import {
  StyleSheet,
  View,
  Text,
  ImageBackground,
  Image,
  Linking,
  ScrollView,
  TouchableOpacity,
} from "react-native";

const ScheduleTourComponent = () => {
  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <View style={styles.container}>
        <View style={styles.title}>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            Schedule a Tour
          </Text>
        </View>
        <Text style={styles.text}>
          {"\n"}
          Tour with the University of Florida College of Design, Construction
          and Planning!
          {"\n"}
          {"\n"}
          Tours are available on weekdays from 10 AM to 5 PM{"\n"}
          Send us an email or schedule one here to let us know you are
          interested!{"\n"}
        </Text>

        <Text style={styles.text}>Schedule a Tour Through Email</Text>
        <TouchableOpacity
          onPress={() => {
            Linking.openURL(
              "mailto:support@example.com?subject=Schedule Tour&body=I would like to schdeule a tour with the UF College of Design, Construction, and Planning Ambassadors. Please list your preferred time and date, as well as first and last name."
            );
          }}
          title="support@example.com"
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            resizeMode="contain"
            source={require("../assets/mail.png")}
          />
          <Text style={styles.linktext}>Email</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
    marginTop: "10%",
  },
  link: {
    flex: 0,
    flexDirection: "row",
    paddingBottom: "3%",
    marginRight: "73%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
  },
  text: {
    color: "white",
    fontWeight: "bold",
    paddingBottom: "2%",
  },
  linktext: {
    color: "white",
    fontWeight: "bold",
    paddingLeft: "5%",
  },
});

export default ScheduleTourComponent;
