import { showLocation } from 'react-native-map-link'
import { View, Text, StyleSheet, ImageBackground } from 'react-native';
import {Button} from 'react-native-elements';
import React from 'react';


const styles = StyleSheet.create({
    container: {
      flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
        marginLeft: '10%',
        marginRight: '10%'
  },
  buttonStyle: {
        padding: 10,
  }
  });

var goTo = (latitude, longitude, title) => {
    showLocation({
        latitude: latitude, 
        longitude: longitude,
        title: title,  // optional
        googleForceLatLon: false,  // optionally force GoogleMaps to use the latlon for the query instead of the title
        alwaysIncludeGoogle: true, // optional, true will always add Google Maps to iOS and open in Safari, even if app is not installed (default: false)
        appsWhiteList: ['google-maps']
    }) 
}

var button = (title, loc, color) => {
    return (
        <View style={styles.buttonStyle}>
            <Button
            title= {title}
            titleStyle={{
                color: "white",
                fontSize: 20,
                fontWeight: 'bold'
            }}
            buttonStyle={{
                backgroundColor: color,
                borderRadius: 100,
            }}
            onPress = {() => goTo(0,0,loc)}
            />
        </View>
    );
}

const Mapa = () => {
    return (
        <ImageBackground source={require('./assets/background.png')} style={{width: '100%', height: '100%'}}>
                <View style={styles.container}>
                    {button('DCP BUILDING','UF DCP','#24449b')}
                    {button('LIBRARY WEST','UF LIBRARY WEST','#ea6227')}
                    {button('MARSTON LIBRARY','UF MARSTON LIBRARY','#24449b')}
                    {button('REITZ UNION','UF REITZ UNION','#ea6227')}
                    {button('BEN HILL GRIFFIN STADIUM','UF Ben Hill Griffin Stadium','#24449b')}
                    {button('RAWLINGS','UF RAWLINGS HALL','#ea6227')}
                </View>
        </ImageBackground>
    );
  };

export default Mapa;
