import React from 'react';
import { View, Text, StyleSheet, Image} from 'react-native';
import {Button} from 'react-native-elements';

 
const styles = StyleSheet.create({
    container: {
      flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
        marginLeft: '30%',
        marginRight: '30%',
  },
  buttonStyle: {
        padding: 5,
  }
  });

const HomeScreen = ({ navigation }) => {
    return (
        <View style={styles.container}>
            <View style={{alignItems: 'center'}}>
                <Image 
                    style={{ width: 250, height: 250 }}
                    source={require('./assets/DCP-Logo.png')} 
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Map'
                titleStyle={{
                    color: "black",
                    fontSize: 16,
                }}
                buttonStyle={{
                    backgroundColor: "green",
                    borderRadius: 60,
                    padding: 5,
                }}
                onPress = {() => navigation.navigate('Map') }
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Calendar'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => navigation.navigate('Calendar')}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'About us'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(3) }
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Events'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Contact Us'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Schedule Tour'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'DCP Login'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
        </View>
    );
  };

export default HomeScreen;
