import React from "react";
import { Font } from "expo";
import { StyleSheet, View, Text, ImageBackground, Image } from "react-native";

const About = () => {
  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <View style={styles.container}>
        <View style={styles.title}>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            College of Design Construction and Planning
          </Text>
          <View
            style={{
              alignItems: "center",
              paddingBottom: "2%",
            }}
          >
            <Image
              style={{ width: 600, height: 250 }}
              resizeMode="contain"
              source={require("../assets/dcp.png")}
            />
          </View>
          <Text
            style={{
              fontSize: 20,
              color: "white",
              fontWeight: "bold",
              paddingBottom: "2%",
            }}
          >
            Vision
          </Text>
          <Text style={styles.text}>
            DCP’s vision is to be recognized globally as a preeminent College
            for teaching, research, creative scholarship, and outreach in the
            built and natural environments.
          </Text>
          <Text
            style={{
              fontSize: 20,
              color: "white",
              fontWeight: "bold",
              paddingBottom: "2%",
            }}
          >
            Mission
          </Text>
          <Text style={styles.text}>
            The mission of the College of Design Construction and Planning is to
            improve the quality of the built and natural environments through
            offering exceptional educational and professional programs and
            research/scholarship initiatives that address the planning, design,
            construction, and preservation of the built and natural
            environments.{" "}
          </Text>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            DCP Ambassadors
          </Text>
          <View
            style={{
              alignItems: "center",
              paddingBottom: "2%",
            }}
          >
            <Image
              style={{ width: 500, height: 250 }}
              resizeMode="contain"
              source={require("../assets/dcpa.png")}
            />
          </View>
          <Text style={styles.text}>
            DCP Ambassadors are a select group of students in the college who
            have demonstrated outstanding academic success and leadership. Their
            objective is to generate interest and knowledge about the college.
          </Text>
          <Text style={styles.text}>List of Programs or Events:</Text>
          <Text style={styles.text}>
            - College Tours{"\n"}- Preview{"\n"}- DCP Career Fair{"\n"}- DCP
            Research Symposium{"\n"}- New Student Showcase{"\n"}- Gator Design &
            Construction Open House{"\n"}- DCP Homecoming Barbeque{" "}
          </Text>
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
    paddingTop: "20%",
  },
  text: {
    color: "white",
    fontWeight: "bold",
    paddingBottom: "2%",
  },
});

export default About;
