import React, { useState } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
} from "react-native";
import { Button } from "react-native-elements";
import { Font } from "expo";
import EventCalendar from "react-native-events-calendar";
import * as Calendar from "expo-calendar";
import * as Permissions from "expo-permissions";

//get the size of device
let { width } = Dimensions.get("window");

const getEvents = () => {
  return events;
};

const Event = () => {
  var events = [];

  const createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  const eventClicked = (event) => {
    alert(
      `${event.title} \nEvent Start: ${event.start}\nEvent End: ${event.end}\nDetails: ${event.summary}
      `
    );
  };

  createEvent(
    "2021-03-14 23:00:00",
    "2021-03-14 23:30:00",
    "Test",
    "This is test"
  );

  return (
    <SafeAreaView style={styles.container}>
      <View style={{ backgroundColor: "red", width: "100%" }}>
        <Button
          title={"Edit"}
          titleStyle={{
            color: "white",
            fontSize: 15,
            fontWeight: "bold",
          }}
          buttonStyle={{
            backgroundColor: "black",
            borderRadius: 60,
          }}
        />
      </View>
      <View style={styles.container}>
        <EventCalendar
          eventTapped={eventClicked}
          events={events}
          width={width}
          size={90}
          scrollToFirst
        />
      </View>
    </SafeAreaView>
  );
};

export default Event;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
