import React, { useState, setState, Component } from "react";
import { ScrollView } from "react-native";
import NativeForms from "native-forms";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  Text,
  TextInput,
} from "react-native";
import { Button } from "react-native-elements";
import EventCalendar from "react-native-events-calendar";

const button = (title, nav, navigation, color) => {
  return (
    <View style={styles.buttonStyle}>
      <Button
        title={title}
        titleStyle={{
          color: "white",
          fontSize: 15,
          fontWeight: "bold",
        }}
        buttonStyle={{
          backgroundColor: color,
          borderRadius: 60,
        }}
        onPress={() => navigation.navigate(nav)}
      />
    </View>
  );
};

//get the size of device
let { width, height } = Dimensions.get("window");
const Event = ({ navigation }) => {
  var events = [];

  const createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  const eventClicked = (event) => {
    alert(
      `${event.title} \nEvent Start: ${event.start}\nEvent End: ${event.end}\nDetails: ${event.summary}
      `
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <View
        style={{
          width: "100%",
          flex: "1",
          alignItems: "flex-end",
          paddingRight: 8,
        }}
      >
        <Button
          title={"Edit"}
          titleStyle={{
            color: "black",
            fontSize: 18,
          }}
          buttonStyle={{
            backgroundColor: "transparent",
          }}
          onPress={() => navigation.navigate("Transition")}
        />
      </View>
      <View style={styles.container}>
        <EventCalendar
          eventTapped={eventClicked}
          events={events}
          width={width}
          size={90}
          scrollToFirst
        />
      </View>
    </SafeAreaView>
  );
};

export default Event;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  buttonStyle: {
    padding: 5,
  },
  edition: {
    backgroundColor: "lightblue",
    flex: 1,
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
    marginTop: "10%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
  },
});

const Transition = ({ navigation }) => {
  var bool = false;
  const [hasForm, showForm] = useState(false);
  const show = () => {
    bool = !bool;
    showForm(bool);
  };
  const hide = () => showForm(false);
  return (
    <ScrollView style={{ backgroundColor: "lightblue" }}>
      <View style={styles.edition}>
        <View style={styles.buttonStyle}>
          <Button
            title={"Tu madre"}
            titleStyle={{
              color: "white",
              fontSize: 15,
              fontWeight: "bold",
            }}
            buttonStyle={{
              backgroundColor: "black",
              borderRadius: 60,
            }}
            onPress={show}
          />
          {hasForm && (
            <NativeForms form="https://my.nativeforms.com/UzY1NncU1jZmgWQzsWUY1Db" />
          )}
          />
        </View>{" "}
      </View>
    </ScrollView>
  );
};

export { Transition };
