import React from 'react';
import { View, Text, StyleSheet, Image, ImageBackground} from 'react-native';
import {Button} from 'react-native-elements';

 
const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
        marginLeft: '30%',
        marginRight: '30%',
        backgroundColor: '#ffebcd'
  },
  buttonStyle: {
        padding: 5,
  }
  });

const HomeScreen = ({ navigation }) => {
    return (
        <ImageBackground source={require('./assets/background.png')} style={{width: '100%', height: '100%'}}>
        <View style={{backgroundColor:'#ffebcd'}}>
            <View style={styles.container}>
                <View style={{alignItems: 'center'}}>
                    <Image 
                        style={{ width: 300, height: 300 }}
                        resizeMode="contain"
                        source={require('./assets/DCP-Logo.png')} 
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'SCHEDULE TOUR'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#24449b",
                        borderRadius: 100,
                    }}
                    onPress = {() => alert(1)}
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'EVENTS'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#ea6227",
                        borderRadius: 100,
                    }}
                    onPress = {() => alert(1)}
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'CALENDAR'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#24449b",
                        borderRadius: 100,
                    }}
                    onPress = {() => navigation.navigate('Calendar')}
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'MAP'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#ea6227",
                        borderRadius: 100,
                    }}
                    onPress = {() => navigation.navigate('Map') }
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'ABOUT US'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#24449b",
                        borderRadius: 100,
                    }}
                    onPress = {() => alert(2)}
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'CONTACT US'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#ea6227",
                        borderRadius: 100,
                    }}
                    onPress = {() => alert(2)}
                    />
                </View>
                <View style={styles.buttonStyle}>
                    <Button
                    title= 'DCP LOGIN'
                    titleStyle={{
                        color: "white",
                        fontSize: 20,
                        fontWeight: 'bold'
                    }}
                    buttonStyle={{
                        backgroundColor: "#24449b",
                        borderRadius: 100,
                    }}
                    onPress = {() => alert(2)}
                    />
                </View>
            </View>
        </View>
        </ImageBackground>
    );
  };

export default HomeScreen;
