import React from "react";
import { StyleSheet, View, Text, ImageBackground, Image } from "react-native";

const About = () => {
  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <View style={styles.container}>
        <View style={styles.title}>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            DCP Ambassadors
          </Text>
          <View style={{ alignItems: "center" }}>
            <Image
              style={{ width: 500, height: 300 }}
              resizeMode="contain"
              source={require("../assets/dcpa.png")}
            />
          </View>
          <Text style={{ color: "white", fontWeight: "bold" }}>
            DCP Ambassadors are a select group of students in the college who
            have demonstrated outstanding academic success and leadership. Their
            objective is to generate interest and knowledge about the college.
          </Text>
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
  },
});

export default About;
