import React from 'react';
import { View, Text, StyleSheet, Button, Image} from 'react-native';
 
const styles = StyleSheet.create({
    container: {
      flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        marginLeft: '30%',
        marginRight: '30%',
        backgroundColor: 'red'
  },
  buttonStyle: {
        padding: 10,
  },
  logo : {
      width: 250,
      height: 250,
      flex: 1,
      
  }
  });

const HomeScreen = ({ navigation }) => {
    return (
        <View style={styles.container}>
            <Image 
                style={styles.logo}
                source={require('./assets/DCP-Logo.png')} 
            />
            <View style={styles.buttonStyle}>
                <Button
                title= 'Map'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => navigation.navigate('Map') }
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Calendar'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => navigation.navigate('Calendar')}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'About us'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(3) }
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Events'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Contact Us'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'Schedule Tour'
                color= 'orange'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
            <View style={styles.buttonStyle}>
                <Button
                title= 'DCP Login'
                color= 'blue'
                fontWeight = 'bold'
                onPress = {() => alert(2)}
                />
            </View>
        </View>
    );
  };

export default HomeScreen;
