/*
 * mostly just tried to mix our existing setup with sample authentication flow code,
 * and try to understand how it worked. wish I was less busy so I could make a few changes,
 * but it should work as a barebones login portal
 */
import * as React from "react";
import {
  StyleSheet,
  Button,
  View,
  Image,
  ImageBackground,
  Text,
  TextInput,
} from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

import CalendarComponent from "./Screens/Calendar";
import MapComponent from "./Screens/Map";
import HomeComponent from "./Screens/Home";

const AuthContext = React.createContext();

function SplashComponent() {
  return (
    <View>
      <Text>Loading...</Text>
    </View>
  );
}

function DashboardComponent() {
  const { signOut } = React.useContext(AuthContext);

  return (
    <View>
      <Text>Signed in!</Text>
      <Button title="Sign out" onPress={signOut} />
    </View>
  );
}

function LoginComponent() {
  const [username, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");

  const { signIn } = React.useContext(AuthContext);

  return (
    <View>
      <TextInput
        placeholder="Username"
        value={username}
        onChangeText={setUsername}
      />
      <TextInput
        placeholder="Password"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />
      <Button title="Sign in" onPress={() => signIn({ username, password })} />
    </View>
  );
}

const Stack = createStackNavigator();

export default function App({ navigation }) {
  const [state, dispatch] = React.useReducer(
    (prevState, action) => {
      switch (action.type) {
        case "RESTORE_TOKEN":
          return {
            ...prevState,
            userToken: action.token,
            isLoading: false,
          };
        case "SIGN_IN":
          return {
            ...prevState,
            isSignout: false,
            userToken: action.token,
          };
        case "SIGN_OUT":
          return {
            ...prevState,
            isSignout: true,
            userToken: null,
          };
      }
    },
    {
      isLoading: true,
      isSignout: false,
      userToken: null,
    }
  );

  React.useEffect(() => {
    // Fetch the token from storage then navigate to our appropriate place
    const bootstrapAsync = async () => {
      let userToken;

      try {
        // Restore token stored in `SecureStore` or any other encrypted storage
        // userToken = await SecureStore.getItemAsync('userToken');
      } catch (e) {
        // Restoring token failed
      }

      // After restoring token, we may need to validate it in production apps

      // This will switch to the App screen or Auth screen and this loading
      // screen will be unmounted and thrown away.
      dispatch({ type: "RESTORE_TOKEN", token: userToken });
    };

    bootstrapAsync();
  }, []);

  const authContext = React.useMemo(
    () => ({
      signIn: async (data) => {
        // In a production app, we need to send some data (usually username, password) to server and get a token
        // We will also need to handle errors if sign in failed
        // After getting token, we need to persist the token using `SecureStore` or any other encrypted storage
        // In the example, we'll use a dummy token

        dispatch({ type: "SIGN_IN", token: "dummy-auth-token" });
      },
      signOut: () => dispatch({ type: "SIGN_OUT" }),
      signUp: async (data) => {
        // In a production app, we need to send user data to server and get a token
        // We will also need to handle errors if sign up failed
        // After getting token, we need to persist the token using `SecureStore` or any other encrypted storage
        // In the example, we'll use a dummy token

        dispatch({ type: "SIGN_IN", token: "dummy-auth-token" });
      },
    }),
    []
  );

  return (
    <AuthContext.Provider value={authContext}>
      <NavigationContainer>
        <Stack.Navigator>
          {state.isLoading ? (
            // We haven't finished checking for the token yet
            <Stack.Screen name="Splash" component={SplashComponent} />
          ) : state.userToken == null ? (
            <>
              <Stack.Screen
                name="Home"
                options={{
                  title: " ",
                  headerTintColor: "#ea6227",
                  headerTransparent: true,
                }}
                component={HomeComponent}
              />
              <Stack.Screen
                name="Calendar"
                options={{
                  title: " ",
                  headerTintColor: "#ea6227",
                  headerTransparent: true,
                }}
                component={CalendarComponent}
              />
              <Stack.Screen
                name="Map"
                options={{
                  title: " ",
                  headerTintColor: "#ea6227",
                  headerTransparent: true,
                }}
                component={MapComponent}
              />
              <Stack.Screen
                name="Login"
                options={{
                  title: " ",
                  headerTintColor: "#ea6227",
                  headerTransparent: true,
                }}
                component={LoginComponent}
              />
            </>
          ) : (
            // User is signed in
            <Stack.Screen
              name="Dashboard"
              component={DashboardComponent}
              options={{
                title: "Sign in",
              }}
            />
          )}
        </Stack.Navigator>
      </NavigationContainer>
    </AuthContext.Provider>
  );
}
