import React, { useState, setState, Component } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
  Text,
} from "react-native";
import { Button } from "react-native-elements";
import EventCalendar from "react-native-events-calendar";
import Modal from "react-native-modal";

const button = (title, nav, navigation, color) => {
  return (
    <View style={styles.buttonStyle}>
      <Button
        title={title}
        titleStyle={{
          color: "white",
          fontSize: 15,
          fontWeight: "bold",
        }}
        buttonStyle={{
          backgroundColor: color,
          borderRadius: 60,
        }}
        onPress={() => navigation.navigate(nav)}
      />
    </View>
  );
};

//get the size of device
let { width, height } = Dimensions.get("window");
const Event = ({ navigation }) => {
  var events = [];

  const createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  const eventClicked = (event) => {
    alert(
      `${event.title} \nEvent Start: ${event.start}\nEvent End: ${event.end}\nDetails: ${event.summary}
      `
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <View
        style={{
          width: "100%",
          flex: "1",
          alignItems: "flex-end",
          paddingRight: 8,
        }}
      >
        <Button
          title={"Edit"}
          titleStyle={{
            color: "black",
            fontSize: 18,
          }}
          buttonStyle={{
            backgroundColor: "transparent",
          }}
          onPress={() => navigation.navigate(nav)}
        />
      </View>
      <View style={styles.container}>
        <EventCalendar
          eventTapped={eventClicked}
          events={events}
          width={width}
          size={90}
          scrollToFirst
        />
      </View>
    </SafeAreaView>
  );
};

export default Event;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  buttonStyle: {
    padding: 5,
  },
});

const Transition = ({ navigation }) => {
  {
    button("ADD EVENTS", "Add events", navigation, "#24449b");
  }
  {
    button("EDIT/DELETE EVENTS", "Delit Events", navigation, "#ea6227");
  }
};
