import React, { useState, setState, Component } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
  Text,
} from "react-native";
import { Button } from "react-native-elements";
import EventCalendar from "react-native-events-calendar";
import Modal from "react-native-modal";

//get the size of device
let { width } = Dimensions.get("window");

export default class Event extends Component {
  constructor(props) {
    super(props);
    var events = [];
    const [isModalVisible, setModalVisible] = useState(false);
  }

  toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  eventClicked = (event) => {
    alert(
      `${event.title} \nEvent Start: ${event.start}\nEvent End: ${event.end}\nDetails: ${event.summary}
      `
    );
  };

  render() {
    return (
      <SafeAreaView style={styles.container}>
        <View
          style={{
            width: "100%",
            flex: "1",
            alignItems: "flex-end",
            paddingRight: 8,
          }}
        >
          <Button
            title={"Edit"}
            titleStyle={{
              color: "black",
              fontSize: 18,
            }}
            buttonStyle={{
              backgroundColor: "transparent",
            }}
            onPress={() => this.setState({ isAboutVisible: true })}
          />
        </View>
        <View>
          {this.state.isAboutVisible ? (
            <Modal transparent={true} visible={this.state.isAboutVisible}>
              <View>
                <Text>TU MadreS</Text>
              </View>
            </Modal>
          ) : null}
        </View>
        <View style={styles.container}>
          <EventCalendar
            eventTapped={this.eventClicked}
            events={this.events}
            width={width}
            size={90}
            scrollToFirst
          />
        </View>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});

const test = () => {
  <Modal>
    <View>
      <Text>This is a test</Text>
    </View>
  </Modal>;
};
