import React from "react";
import { Font } from "expo";
import {
  StyleSheet,
  View,
  Text,
  ImageBackground,
  Image,
  Linking,
  ScrollView,
  TouchableOpacity,
} from "react-native";

const Contact = () => {
  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <View style={styles.container}>
        <View style={styles.title}>
          <Text
            style={{
              fontSize: 24,
              textAlign: "center",
              paddingBottom: "2%",
              color: "white",
              fontWeight: "bold",
            }}
          >
            Contact Us
          </Text>
        </View>
        <Text style={styles.text}>
          {"\n"}
          University of Florida College of Design, Construction and Planning
          {"\n"}
          {"\n"}
          Address: Architecture Building, 1480 Inner Road, Gainesville, FL 32611
          {"\n"}
          Phone Number: (352) 392-4836{"\n"}
          Fax: (352) 392-7266{"\n"}
          =========== ADD MORE INFO=========
        </Text>
        <Text style={styles.text}>Connect:</Text>
        <TouchableOpacity
          onPress={() => {
            Linking.openURL("mailto:support@example.com");
          }}
          title="support@example.com"
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            resizeMode="contain"
            source={require("../assets/mail.png")}
          />
          <Text style={styles.linktext}>Email</Text>
        </TouchableOpacity>

        <TouchableOpacity
          onPress={() => {
            Linking.openURL("https://instagram.com/ufdcp/");
          }}
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            resizeMode="contain"
            source={require("../assets/ig.png")}
          />
          <Text style={styles.linktext}>Instagram</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            Linking.openURL("https://twitter.com/ufdcp");
          }}
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            source={require("../assets/twitter.png")}
            resizeMode="contain"
          />
          <Text style={styles.linktext}>Twitter</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            Linking.openURL("https://www.facebook.com/UF.dcp");
          }}
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            resizeMode="contain"
            source={require("../assets/fb.png")}
          />
          <Text style={styles.linktext}>Facebook</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            Linking.openURL("https://www.linkedin.com/company/ufdcp/");
          }}
          style={styles.link}
        >
          <Image
            style={{ width: 23, height: 23 }}
            resizeMode="contain"
            source={require("../assets/ld.png")}
          />
          <Text style={styles.linktext}>Linkedin</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: "100%",
    marginLeft: "7%",
    marginRight: "7%",
    marginTop: "10%",
  },
  link: {
    flex: 0,
    flexDirection: "row",
    paddingBottom: "3%",
    marginRight: "73%",
  },
  title: {
    height: "10%",
    paddingTop: "10%",
  },
  text: {
    color: "white",
    fontWeight: "bold",
    paddingBottom: "2%",
  },
  linktext: {
    color: "white",
    fontWeight: "bold",
    paddingLeft: "5%",
  },
});

export default Contact;
