import React, { useState } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  Dimensions,
  ImageBackground,
} from "react-native";
import EventCalendar from "react-native-events-calendar";
import * as Calendar from "expo-calendar";
import * as Permissions from "expo-permissions";

//get the size of device
let { width } = Dimensions.get("window");

const Event = () => {
  const createEvent = (start, end, title, summary) => {
    events.push({
      start: start,
      end: end,
      title: title,
      summary: summary,
    });
  };

  const eventClicked = (event) => {
    openCalendarRequest();
  };

  createEvent(
    "2021-03-14 23:00:00",
    "2021-03-14 23:30:00",
    "Test",
    "This is test"
  );

  const [granted, setGranted] = useState(false);
  const [eventIdInCalendar, setEventIdInCalendar] = useState("");

  const openCalendarRequest = async () => {
    const { status } = await Permissions.askAsync(Permissions.CALENDAR);
    if (status === "granted") {
      setGranted(true);
    }
  };

  useEffect(() => {
    openCalenarRequest(); // Ask for Premission to access phone calendar
  }, []);
  const eventDetails = {
    title: "Event Title",
    startDate: new Date("2019-12-14 07:00"),
    endDate: new Date("2019-12-14 15:00"),
  };

  const addEventToCalendar = async (eventDetails) => {
    const eventIdInCalendar = await Calendar.createEventAsync(
      "1",
      eventDetails
    );
    Calendar.openEventInCalendar(eventIdInCalendar); // that will give the user the ability to access the event in phone calendar
    setEventIdInCalendar(eventIdInCalendar);
  };

  return (
    <ImageBackground
      source={require("../assets/background.png")}
      style={{ width: "100%", height: "100%" }}
    >
      <SafeAreaView style={styles.container}>
        <View style={styles.container}>
          <EventCalendar
            eventTapped={addEventToCalendar}
            events={events}
            width={width - width * 0.1}
            size={90}
            scrollToFirst
          />
        </View>
      </SafeAreaView>
    </ImageBackground>
  );
};

export default Event;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
